import React, {Component} from "react";
import {Bar,Line,Pie} from 'react-chartjs-2';


class Chart extends Component {
    constructor(props) {
        super(props);
        this.state =  {
            chartData: {
                labels: ['T1','T2','T3'],
                datasets:[{
                    label:'Total Hours',
                    data:[5,3,10]
                }],
                backgroundColor:[
                    'rgba(255,99,132,.6)',
                    'rgba(54,162,235,.6)',
                    'rgba(255,206,86,.6)',
                    
                ]
            }
        }

    }

    formatData() {
        //TODO
        return;
    }


    render() {
        return (
            <div className = "BarChart">
                <Bar data={this.state.chartData} 
                    width={100}
                    height={50}
                    options={{ maintainAspectRatio: false }}
                />
            </div>
        );

    }
}

export default Chart;
