import React, {Component} from "react";
import "./Timer.css"
import AccessAlarmIcon from '@material-ui/icons/AccessAlarm';


class Timer extends Component {
    constructor(props) {
        super(props);
        this.timer = {
            hours:'01',
            minutes:'00',
            seconds:'00'
        }
        this._inputHours= "";
        this._inputMinutes = "";
        this._inputSeconds = "";
    }

    //Given any input from the input boxes transform seconds to minutes and minutes to hours
    transformTime() {
        console.log(this.timer.seconds > 60);
        while (this.timer.seconds > 60 || this.timer.minutes > 60) {
            if (this.timer.seconds > 60) {
                this.timer.seconds -= 60
                this.timer.minutes += 1
            }
            if (this.timer.minutes > 60) {
                this.timer.minutes -= 60
                this.timer.hours += 1
            }
        }
    }

    startTimer(e) {
        this.transformTime()
        
    }

    checkTimer() {
        return this.props.starter;
    }

    updateTime() {
        console.log('input is: ',this._inputHours,typeof(this._inputHours));
        if (this._inputHours !== null && this._inputHours !== "" && this._inputHours.value !== "" ) {
            this.timer.hours = this._inputHours.value;           
        }

        if (this._inputMinutes !== null && this._inputMinutes !== "" && this._inputMinutes !== "" ) {
            this.timer.minutes = this._inputMinutes.value;           
        }

        if(this._inputSeconds !== null && this._inputSeconds !== "" && this._inputSeconds.value !== "" ) {
            this.timer.seconds = this._inputSeconds.value;
        }
    }
    
    render () {
        // Update time if any new input was provided
        this.updateTime();
        //Convert seconds to minutes and minutes to hours if needed;
        this.updateTime();
        // if TodoList indicates not to start keep displaying the edit timer.
        if (!this.checkTimer()) {
            return (
                <div className="timer-editor">
                    <table>
                        <tbody>
                            <tr className = "text-timer">
                                <th>Hours</th>
                                <th>Minutes</th>
                                <th>Seconds</th>
                            </tr>
                        </tbody>
                    
                        <tr className="input-timer">
                            <td>
                                <input
                                ref={ (a) => this._inputHours = a} 
                                placeholder={this.timer.hours}
                                >
                                </input>
                            </td>
                            <td>
                                <input 
                                    ref={ (a) => this._inputMinutes = a}
                                    placeholder={this.timer.minutes}></input>
                            </td>
                            <td>
                                <input
                                ref= { (a) => this._inputSeconds = a}
                                placeholder={this.timer.seconds}></input>
                            </td>
                            <td><AccessAlarmIcon/></td>
                        </tr>
                        
                    </table>
                </div>
                
            );
        } else { // display timer countdown 
            return (
                
                    <p className="timer-countdown">{this.timer.hours + ':'+this.timer.minutes + ':' + this.timer.seconds} <AccessAlarmIcon/></p>
            );
        }
    }
}
export default Timer;
