import React, {Component} from "react";
import {Bar,Line,Pie} from 'react-chartjs-2';


class Chart extends Component {
    constructor(props) {
        super(props);
        this.state =  {
            chartData: {
                labels: ['T1','T2','T3','T4'],
                datasets:[{
                    label:'Total Hours',
                    data:[2,4,3,1],
                    backgroundColor:'#ccffcc',
                }],
            }
        }

    }

    componentWillMount() {
        var Data = {...this.state.chartData};
        Data.labels = this.props.ChartData.labels;
        Data.data = this.props.ChartData.data;
        //update component with new data
        this.setState({chartData:Data});
    }


    render() {
        return (
            <div className = "BarChart">
                <Bar data={this.state.chartData} 
                    width={50}
                    height={30}
                    options={
                        {
                            legend: {
                              display: false
                            },
                            scales: {
                              yAxes: [{
                                ticks: {
                                   beginAtZero:true,
                                   stepSize:1,
                                 }
                               }]
                              }
                        }
                    }
                />
            </div>
        );

    }
}

export default Chart;
